import React, { useState } from 'react';
import "./items-list.css";

export function ItemsList(props){
    const [item, setTotalItem] = useState(0);

    const incrementItem = () => {
      setTotalItem(prevValue => prevValue + 1);
    };

    const decrementItem = () => {
      if (item > 0) {
        setTotalItem(prevValue => prevValue - 1);
      }
    };

    return <div className='items-list'>
      <h2>Price</h2>
      <span className='title'>{props.title}</span>
      <div className="quantity-container">

        <button className="decrement-button" onClick={decrementItem}>-</button>

        <span className='item-quantity'>{item}</span>

        <button className="increment-button" onClick={incrementItem}>+</button>

      </div>
    </div>
}
