import React, { useState } from 'react';

const Items = () => {

  const [customerId, setCustomerId] = useState('');
  const [name, setName] = useState('');
  const [phoneNumber, setPhoneNumber] = useState('');

  const [item, setTotalItem] = useState(0);

  const incrementItem = () => {
    setTotalItem(prevValue => prevValue + 1);
  };

  const decrementItem = () => {
    if (item > 0) {
      setTotalItem(prevValue => prevValue - 1);
    }
  };


  return (
    <div>
      <input type="text" placeholder="Automatic ID" value={customerId} onChange={e => setCustomerId(e.target.value)} />

      <input type="text" placeholder="Name" value={name} onChange={e => setName(e.target.value)} />

      <input type="text" placeholder="Phone Number" value={phoneNumber} onChange={e => setPhoneNumber(e.target.value)} />

        <div>
      <span> Playeras/Camisas: </span>
        <button onClick={decrementItem}>-</button>
        <span>{item}</span>
        <button onClick={incrementItem}>+</button>
      </div>
      <div>
      <span> Shorts/Bermudas: </span>
        <button onClick={decrementItem}>-</button>
        <span>{item}</span>
        <button onClick={incrementItem}>+</button>
      </div>




    </div>
  )


};

export default Items;
